mapper.insert:
   
	    	SELECT MP_BOARD_SEQ.NEXTVAL FROM DUAL
	    
	   INSERT INTO MP_BOARD(    BNO 
	   					      , TITLE 
	   					      , CONTENT 
	   					      , WRITER
	   					      , review_Email
	   					      , review_Password
	   					      ,review_Image
	   					      )
	                 VALUES(    MP_BOARD_SEQ.NEXTVAL 
	                 		  , #{title}
	                 		  , #{content}
	                 		  , #{writer}
	                 		  , #{review_Email}
	                 		  , #{review_Password}
	                 		  ,#{review_Image} 
	                 		  )

mapper.update:
update  MP_BOARD set title=#{title},
                    content=#{content},
                     review_Email=#{review_Email},
                      review_Password=#{review_Password}
                   where BNO=#{bno}

update  MP_BOARD set 
readCount=readCount+1 
where BNO=#{bno}

UPDATE MP_FILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}
