mapper.insert:
		insert into notice(
		notice_Number,
		notice_Subject,
		notice_Content,
		notice_Image
		)
		values(notice_seq.nextval
		,#{notice_Subject}
		,#{notice_Content}
		,#{notice_Image}
		)

mapper.update:
update notice set notice_Subject=#{notice_Subject},
                    notice_Content=#{notice_Content},
                   notice_Image=#{notice_Image}
     where notice_Number=#{notice_Number}

update notice set 
notice_Readcount=notice_Readcount+1 
where notice_Number=#{notice_Number}
